import { useState } from 'react';
import Header from './components/Header';
import CalculatorForm from './components/CalculatorForm';
import PriceResult from './components/PriceResult';

const App = () => {
  const [result, setResult] = useState(null);

  const calculatePrice = (data) => {
    const { fixedCosts, equipmentCosts, materials, operationalCosts, hours, marketRate } = data;

    // Cálculo del precio base
    const hourlyRate = (fixedCosts / 160) + equipmentCosts + (marketRate * 0.5);
    const basePrice = hourlyRate * hours;

    // Suma de costos directos
    const totalCosts = basePrice + materials + operationalCosts;

    // Margen de ganancia (30%)
    const finalPrice = totalCosts * 1.3;

    setResult({
      basePrice: Math.round(basePrice),
      totalCosts: Math.round(totalCosts),
      finalPrice: Math.round(finalPrice)
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <main className="container mx-auto p-6 max-w-2xl">
        <div className="bg-white p-8 rounded-xl shadow-lg">
          <h2 className="text-2xl font-bold mb-6">Calcula tu tarifa creativa</h2>

          <CalculatorForm onCalculate={calculatePrice} />

          {result && (
            <div className="mt-8 space-y-4">
              <PriceResult title="Precio base (sin ganancia)" value={result.basePrice} />
              <PriceResult title="Costos totales" value={result.totalCosts} />
              <PriceResult title="Precio final recomendado" value={result.finalPrice} />
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default App;

